mapper.select:

select 

from PIP_COMM_PATIENT
where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and PROJECT_ID = #{projectId,jdbcType=VARCHAR}

select a.PATIENT_ID, a.PROJECT_ID, a.PATIENT_NAME, a.COMM_PERSON_CODE, a.CREATE_BY, b.lcc_name, a.CREATE_DATE, a.UPDATE_BY, a.UPDATE_DATE, a.IS_REMOVED, a.SEX, a.NATION, a.BIRTHDAY, a.NATIONALITY, a.ID_NUMBER, a.CREDENTIALS_TYPE, a.CREDENTIALS_CODE, a.PHONE, a.MOBILE, a.EMAIL, a.MARITAL_STATUS, a.EDUCATIONAL, a.POST_CODE, a.NATIVE_PROVENCE_CODE, a.NATIVE_CITY_CODE, a.NATIVE_COUNTY_CODE, a.NATIVE_TOWN_CODE, a.NATIVE_VILLAGE_CODE, a.NOW_PROVENCE_CODE, a.NOW_CITY_CODE, a.NOW_COUNTY_CODE, a.NOW_TOWN_CODE, a.NOW_VILLAGE_CODE, a.LCC_CODE, a.PATIENT_CODE, a.BLOOD_CODE, a.IS_RISK, RISK_DATE, a.IS_FOLLOWVIEW, a.FOLLOWVIEW_DATE, a.IS_FOLLOWRISK, a.FOLLOWRISK_DATE, a.RISK_CODE, a.IS_SPECIAL, a.LINK_MAN1, a.LINK_MAN1_RELATION,a.LINK_MAN1_MOBILE,a.LINK_MAN2,a.LINK_MAN2_RELATION,a.LINK_MAN2_MOBILE, a.CASE_CODE,a.LINK_MAN1_PHONE,a.LINK_MAN2_PHONE,a.LAST_FOLLOWVIEW_DATE, a.BELONG_GROUP,a.GROUP_DATE,a.IS_SMOKING,a.IS_SUBSCRIBE,a.IS_HYPERTENSION,a.IS_JOIN_MSG,a.IS_DIABETES,a.IS_SYSTEM_DIABETES, uqsState.STATE_010,uqsState.STATE_011,uqsState.STATE_012,uqsState.STATE_002002,STATE_014,STATE_015 ,a.is_6state , a.IS_DEAD, round( sysdate - nvl(a.group_date,pd.six_real_date) ) between_days from PIP_COMM_PATIENT a left join Pip_Comm_Lcc b on a.lcc_code = b.lcc_code and b.project_id= a.project_id left join PIP_COMM_PATIENT_DATE pd on a.PATIENT_ID = pd.PATIENT_ID and pd.project_id = a.project_id left join(select PROJECT_ID,PATIENT_ID, max(case when substr(UQS_VERSION,5,3)='010' then STATE else null end)as STATE_010, max(case when substr(UQS_VERSION,5,3)='011' then STATE else null end)as STATE_011, max(case when substr(UQS_VERSION,5,3)='012' then STATE else null end)as STATE_012, max(case when substr(UQS_VERSION,5,3)='014' then STATE else null end)as STATE_014, max(case when substr(UQS_VERSION,5,3)='015' then STATE else null end)as STATE_015, max(case when substr(UQS_VERSION,5,7)='002.002' then STATE else null end)as STATE_002002 from(SELECT PROJECT_ID,PATIENT_ID,UQS_VERSION,STATE,Row_Number() OVER (partition by PROJECT_ID,PATIENT_ID,substr(UQS_VERSION,5,3) ORDER BY STATE,CREATE_DATE desc) rank FROM PIP_UQS_ANSWERQN_LOG where STATE<=2)where rank=1 group by PROJECT_ID,PATIENT_ID)uqsState on uqsState.PROJECT_ID=a.PROJECT_ID and uqsState.PATIENT_ID=a.PATIENT_ID where a.PROJECT_ID =#{projectId,jdbcType=VARCHAR}
and a.process_types = #{processtypes}
and substr(a.patient_id,4,2) = '99'
and substr(a.patient_id,4,2) != '99'
and a.IS_JOIN_MSG=#{isJoinMsg}
and a.is_removed = #{isRemoved}
and length(a.patient_id) = #{idlength}
and ( a.IS_JOIN_MSG !='1' or a.IS_JOIN_MSG is null)
and a.lcc_code in ( select lcc_code from pip_sys_user_data_limit lt where lt.user_id =#{userId} )
and pd.FIRST_PLAN_DATE >= #{firstStartDate}
and pd.FIRST_PLAN_DATE <= #{firstEndDate}
and pd.SIX_PLAN_DATE >= #{sixStartDate}
and pd.SIX_PLAN_DATE <= #{sixEndDate}
and a.lcc_code = #{lccCode}
and a.PATIENT_ID = #{patientId}
and a.PATIENT_NAME like '%'||#{patientName}||'%'
and a.ID_NUMBER like '%'||#{idNumber}||'%'
and a.BELONG_GROUP = #{belongGroup}
and a.IS_DIABETES = #{isdiabetes}
and not exists(select 1 from PIP_UQS_ANSWERQN_LOG l where l.PATIENT_ID=a.PATIENT_ID and l.PROJECT_ID=a.PROJECT_ID and l.STATE=1 and substr(l.UQS_VERSION,5,3) in (${noQn}))
and exists(select 1 from PIP_UQS_ANSWERQN_LOG l where l.PATIENT_ID=a.PATIENT_ID and l.PROJECT_ID=a.PROJECT_ID and l.STATE=1 and substr(l.UQS_VERSION,5,3) in (${completeQn}))
and a.IS_SYSTEM_DIABETES = 1
and ( (a.IS_SYSTEM_DIABETES is null AND STATE_012 is not null ) or a.IS_SYSTEM_DIABETES = 2)
and (a.IS_DEAD is null or a.IS_DEAD = 2)
AND STATE_010 is null AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND STATE_010 = '1' --AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND STATE_010 = '2' AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND a.IS_DEAD = '1' and not exists( select 1 from pip_uqs_answerqn_log ql where a.patient_id = ql.patient_id and ql.state=1 and ql.uqs_version = '004.010.001' )
AND STATE_011 is null AND ( a.IS_DEAD != '1' or a.IS_DEAD is null ) and STATE_010 = '1'
AND STATE_011 = '1' --AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND STATE_011 = '2' AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND a.IS_DEAD = '1' and not exists( select 1 from pip_uqs_answerqn_log ql where a.patient_id = ql.patient_id and ql.state=1 and ql.uqs_version = '004.011.001' )
AND STATE_012 is null AND ( a.IS_DEAD != '1' or a.IS_DEAD is null ) and STATE_011 = '1' and ( a.IS_DIABETES is null or a.IS_DIABETES != '1' )
AND STATE_012 = '1' --AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND STATE_012 = '2' AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND a.IS_DEAD = '1' and not exists( select 1 from pip_uqs_answerqn_log ql where a.patient_id = ql.patient_id and ql.state=1 and ql.uqs_version = '004.012.001' )
AND STATE_002002 is null AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND STATE_002002 = '1' --AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND STATE_002002 = '2' AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND a.IS_DEAD = '1' and not exists( select 1 from pip_uqs_answerqn_log ql where a.patient_id = ql.patient_id and ql.state=1 and ql.uqs_version = '004.002.002' )
AND STATE_015 is null AND ( a.IS_DEAD != '1' or a.IS_DEAD is null ) and STATE_002002 = '1' and round( sysdate - nvl(a.group_date,pd.six_real_date) ) >= 150
AND STATE_015 = '1' --AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND STATE_015 = '2' AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND a.IS_DEAD = '1' and not exists( select 1 from pip_uqs_answerqn_log ql where a.patient_id = ql.patient_id and ql.state=1 and ql.uqs_version = '004.015.001' )
AND STATE_014 is null AND ( a.IS_DEAD != '1' or a.IS_DEAD is null ) and a.belong_group is not null and round( sysdate - nvl(a.group_date,pd.six_real_date) ) >= 150
AND STATE_014 = '1' --AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND STATE_014 = '2' AND ( a.IS_DEAD != '1' or a.IS_DEAD is null )
AND a.IS_DEAD = '1' and not exists( select 1 from pip_uqs_answerqn_log ql where a.patient_id = ql.patient_id and ql.state=1 and ql.uqs_version = '004.014.001' )
order by ${sidx}
${sord}

mapper.delete:

delete from PIP_COMM_PATIENT
where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and PROJECT_ID = #{projectId,jdbcType=VARCHAR}

mapper.update:
update PIP_COMM_PATIENT


    PHONE = #{phone,jdbcType=VARCHAR},


    MOBILE = #{mobile,jdbcType=VARCHAR},


    EMAIL = #{email,jdbcType=VARCHAR},

    PATIENT_NAME = #{patientName,jdbcType=VARCHAR}

where PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and PROJECT_ID = #{projectId,jdbcType=VARCHAR}

mapper.select:
  		select l.project_id , l.lcc_code , l.lcc_name as name , l.workload, count(1) as value
  		from pip_comm_patient p , pip_comm_lcc l
		where  p.risk_code is not null and p.project_id = l.project_id and p.lcc_code = l.lcc_code 
		and p.project_id = #{projectId} and p.lcc_code = #{lccCode} 

			 and p.risk_date >= #{start} 


			 and p.risk_date <= #{end} 

		group by l.project_id, l.lcc_code, l.lcc_name , l.workload

  		select l.project_id , l.lcc_code , l.lcc_name as name , l.workload, count(1) as value
  		from pip_comm_patient p , pip_comm_lcc l
		where p.IS_FOLLOWVIEW = '1' and p.project_id = l.project_id and p.lcc_code = l.lcc_code 
		and p.project_id = #{projectId} and p.lcc_code = #{lccCode} 

			 and p.FOLLOWVIEW_DATE >= #{start} 


			 and p.FOLLOWVIEW_DATE <= #{end} 

		group by l.project_id, l.lcc_code, l.lcc_name , l.workload





             To_Char(A.Create_Date,'yyyy-MM-dd')


            To_Char(A${index}.Create_Date,'yyyy-MM-dd')





           Count(1) Total_Sum  From Pip_Comm_Patient_Qc A 


            inner Join Pip_Comm_Patient_Qc A${index}  On  A.Patient_Id=A${index}.Patient_Id and A.Lcc_Code=A${index}.Lcc_Code and A.Project_Id=A${index}.Project_Id 




              A.ITEM_CODE= #{item}


            and A${index}.ITEM_CODE=#{item}



           and A.Project_Id=#{projectId}


               and A.Lcc_Code=#{lccCode} 




                 and A.CREATE_DATE <= #{end} 



               and  A.Lcc_Code in (select Lcc_Code From Pip_Comm_Lcc Lc  Where  Lc.Area_Code=#{area_code}) 




            To_Char(A.Create_Date,'yyyy-MM-dd')


            To_Char(A${index}.Create_Date,'yyyy-MM-dd')




            Union Select #{item} As Cd,0 As Total_Sum From Dual







select 



     Sum(Decode(cd,#{item},Total_Sum)) As X${index}



from (
select a.cd cd, max(a.total_sum) as mtotal_sum , sum (b.total_sum) as total_sum 
from  sq a  join sq  b  on 1=1 
where   
b.cd <= a.cd   
group by a.cd   order by a.cd
)

mapper.update:
	update PIP_COMM_PATIENT_INFO set PROJECT_ID = #{projectId}

    	,MEDICALHIS_MI = #{medicalHis_MI}


    	,MEDICAL_INSURANCE = #{medicalInsurance}

    where PROJECT_ID = #{projectId} and PATIENT_ID=#{patientId}

	update PIP_COMM_PATIENT_DATE set PROJECT_ID = #{projectId}

		,SIX_REAL_DATE = #{sixRealDate}


		,TWELVE_PLAN_DATE = #{twelvePlanDate}


    where PROJECT_ID = #{projectId} and PATIENT_ID=#{patientId}

mapper.select:
select p.*,lcc.lcc_name,
info.MEDICALHIS_MI as medicalHis_MI, info.MEDICAL_INSURANCE as medicalInsurance, info.SEX as sex, info.AGE_GRADE as age_grade, info.EDU_LEVEL as edu_level, (case when p.IS_DIABETES=1 then 1 else 2 end)is_diabetes from PIP_COMM_PATIENT p left join pip_comm_lcc lcc on p.lcc_code = lcc.lcc_code left join PIP_COMM_PATIENT_INFO info on p.PROJECT_ID=info.PROJECT_ID and p.PATIENT_ID=info.PATIENT_ID where p.PATIENT_ID like '%'||#{q}||'%' and p.process_types = '1' and ( p.is_join_msg != '1' or p.is_join_msg is null ) and ( p.is_dead ='' or p.is_dead is null ) and ( p.belong_group ='' or p.belong_group is null ) and ( p.is_removed = '0') and length(p.patient_id) = 8
and rownum <= #{limit}
