mapper.select:

select 

from PIP_COMM_PATIENT_QA
where LCC_CODE = #{lccCode,jdbcType=VARCHAR}
  and PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and PROJECT_ID = #{projectId,jdbcType=VARCHAR}

mapper.delete:

delete from PIP_COMM_PATIENT_QA
where LCC_CODE = #{lccCode,jdbcType=VARCHAR}
  and PATIENT_ID = #{patientId,jdbcType=VARCHAR}
  and PROJECT_ID = #{projectId,jdbcType=VARCHAR}
