mapper.select:
select 

from PIP_MMS_ACTIVE_TYPE 
where 1=1 

	and state=#{state}


	and project_id=#{projectId}

select PM.*, PT.ACTIVECLASS_NAME, PS.SUPPLIER_NAME, MS.STOCK_NAME from PIP_MMS_DEVIMPORT_MASTER PM LEFT JOIN PIP_MMS_ACTIVE_TYPE PT ON PM.ACTIVECLASS_CODE=PT.ACTIVECLASS_CODE LEFT JOIN PIP_MMS_SUPPLIER PS ON PM.SUPPLIER_CODE=PS.SUPPLIER_CODE LEFT JOIN PIP_MMS_STOREHOUSE MS ON PM.STOCK_CODE=MS.STOCK_CODE and PM.PROJECT_ID=MS.PROJECT_ID where 1=1 and PM.IS_REMOVED=0
and PM.IMORDER_NO = #{imorderNo,jdbcType=VARCHAR}
and PM.PROJECT_ID = #{projectId,jdbcType=VARCHAR}
and MS.PROJECT_ID= #{projectId,jdbcType=VARCHAR}

    select 
	   	PM.*
    from PIP_MMS_DEVIMPORT_MASTER PM
  where 1=1 

		and	PM.IMORDER_NO = #{imorderNo}


		and PM.PROJECT_ID = #{projectId,jdbcType=VARCHAR}

select PM.*, PT.ACTIVECLASS_NAME, PS.SUPPLIER_NAME, MS.STOCK_NAME, PC.LCC_NAME lccName from PIP_MMS_DEVIMPORT_MASTER PM LEFT JOIN PIP_MMS_ACTIVE_TYPE PT ON PM.ACTIVECLASS_CODE=PT.ACTIVECLASS_CODE LEFT JOIN PIP_MMS_SUPPLIER PS ON PM.SUPPLIER_CODE=PS.SUPPLIER_CODE LEFT JOIN PIP_MMS_STOREHOUSE MS ON PM.STOCK_CODE=MS.STOCK_CODE AND PM.PROJECT_ID=MS.PROJECT_ID LEFT JOIN PIP_COMM_LCC PC ON PM.LCC_CODE=PC.LCC_CODE AND PM.PROJECT_ID=PC.PROJECT_ID AND PM.LCC_CODE=PC.LCC_CODE where 1=1 and PM.IS_REMOVED=0 and pm.lcc_code in (select lcc_code from pip_sys_user_data_limit lt where lt.user_id = #{userId})
and PM.IMORDER_NO in (select distinct IMORDER_NO from PIP_MMS_DEVIMPORT_DETAIL where ARCHIVES_NO like '%${archivesNo}%')
and to_char(PM.CREATE_DATE,'yyyy-MM-dd') >= '${orderStartDate}'
and to_char(PM.CREATE_DATE,'yyyy-MM-dd') <= '${orderEndDate}'
and PM.IMORDER_NO like '%${imorderNo}%'
and PM.PROJECT_ID = #{projectId,jdbcType=VARCHAR}
and MS.STOCK_NAME like '%${stockName}%'
and PM.ACTIVECLASS_CODE=#{activeclassCode}
order by PM.${sidx} ${sord}

mapper.delete:
update PIP_MMS_DEVIMPORT_MASTER
set IS_REMOVED=1
where IMORDER_NO = #{imorderNo,jdbcType=VARCHAR}
  and PROJECT_ID = #{projectId,jdbcType=VARCHAR}

mapper.select:
  	  select MD.*
	  from PIP_MMS_DEVIMPORT_DETAIL MD
	  where 1=1

       and MD.PROJECT_ID = #{projectId,jdbcType=VARCHAR}


       and MD.ARCHIVES_NO = #{archivesNo,jdbcType=VARCHAR}

  	  select MD.*,MI.MATERL_NAME materlName,MI.CLASS_CODE classCode,MS.SUPPLIER_NAME supplierName
		from
		PIP_MMS_DEVIMPORT_DETAIL MD
		left join PIP_MMS_MATERL_INFO MI
		on MD.MATERLINFO_CODE=MI.MATERLINFO_CODE
		left join PIP_MMS_SUPPLIER MS
		ON MD.SUPPLIER_CODE=MS.SUPPLIER_CODE
	  where 1=1

       and MD.ID= #{id,jdbcType=DECIMAL}


       and MD.PROJECT_ID = #{projectId,jdbcType=VARCHAR}


       and MD.IMORDER_NO = #{imorderNo,jdbcType=VARCHAR}


       and MD.MATERLINFO_CODE = #{materlinfoCode,jdbcType=VARCHAR}

  	  select MD.*,MI.MATERL_NAME materlName,MI.CLASS_CODE classCode,MS.SUPPLIER_NAME supplierName,
		pm.create_date as order_date,lcc.lcc_name,lcc.lcc_code
		from
		PIP_MMS_DEVIMPORT_DETAIL MD
		left join PIP_MMS_MATERL_INFO MI
		on MD.MATERLINFO_CODE=MI.MATERLINFO_CODE
		left join PIP_MMS_SUPPLIER MS
		ON MD.SUPPLIER_CODE=MS.SUPPLIER_CODE
		left join PIP_MMS_DEVIMPORT_MASTER pm
		on pm.imorder_no= md.imorder_no and pm.project_id = md.project_id
		left join pip_comm_lcc lcc on lcc.lcc_code = pm.lcc_code and pm.project_id = lcc.project_id
	  where 1=1

	  	and pm.lcc_code in (select lcc_code from pip_sys_user_data_limit  lt where lt.user_id = #{userId})


       and MD.ID = = #{id,jdbcType=DECIMAL}


       and MD.ARCHIVES_NO like '%'||#{archivesNo,jdbcType=DECIMAL}||'%'


       and PM.CREATE_DATE >= #{orderStartDate,jdbcType=DECIMAL}


       and PM.CREATE_DATE <= #{orderEndDate,jdbcType=DECIMAL}



       and MD.PROJECT_ID = #{projectId,jdbcType=VARCHAR}


       and MD.IMORDER_NO = #{imorderNo,jdbcType=VARCHAR}


       and MD.MATERLINFO_CODE = #{materlinfoCode,jdbcType=VARCHAR}


		order by MD.${sidx} ${sord}

  	  select MD.*,MI.MATERL_NAME materlName,MI.CLASS_CODE classCode,MS.SUPPLIER_NAME supplierName,
		pm.create_date as order_date,lcc.lcc_name,lcc.lcc_code
		from
		PIP_MMS_DEVIMPORT_DETAIL MD
		left join PIP_MMS_MATERL_INFO MI
		on MD.MATERLINFO_CODE=MI.MATERLINFO_CODE
		left join PIP_MMS_SUPPLIER MS
		ON MD.SUPPLIER_CODE=MS.SUPPLIER_CODE
		left join PIP_MMS_DEVIMPORT_MASTER pm
		on pm.imorder_no= md.imorder_no and pm.project_id = md.project_id
		left join pip_comm_lcc lcc on lcc.lcc_code = pm.lcc_code and pm.project_id = lcc.project_id
	  where 1=1

       and MD.ID = = #{id,jdbcType=DECIMAL}


       and MD.ARCHIVES_NO like '%'||#{archivesNo,jdbcType=DECIMAL}||'%'


       and PM.CREATE_DATE >= #{orderStartDate,jdbcType=DECIMAL}


       and PM.CREATE_DATE <= #{orderEndDate,jdbcType=DECIMAL}



       and MD.PROJECT_ID = #{projectId,jdbcType=VARCHAR}


       and MD.IMORDER_NO = #{imorderNo,jdbcType=VARCHAR}


       and MD.MATERLINFO_CODE = #{materlinfoCode,jdbcType=VARCHAR}


		order by MD.${sidx} ${sord}

select count(*)
from PIP_MMS_DEVIMPORT_DETAIL PD
where PD.IMORDER_NO=#{imorderNo}

   select
		PS.*
		from PIP_MMS_DEVSTORE PS
	where 1=1 

	    and ps.archives_No =#{archivesNo} 

     and  PS.PROJECT_ID=#{projectId}
     and  PS.LCC_CODE=#{lccCode}
     and  PS.MATERLINFO_CODE=#{materlinfoCode}
     and  PS.MATERL_BATCH=#{materlBatch}
     and  PS.stock_Code=#{stockCode}
     and  PS.PERIOD_VALIDITY=#{periodValidity,jdbcType=DATE}

  	select distinct PM.STOCK_CODE from PIP_MMS_DEVIMPORT_MASTER PM
		left join PIP_MMS_DEVIMPORT_DETAIL PD
		ON PM.PROJECT_ID=PD.PROJECT_ID AND PM.IMORDER_NO=PD.IMORDER_NO
	where 1=1
	and PM.PROJECT_ID=#{projectId}
	and PM.IMORDER_NO=#{imorderNo}
	and PD.ID=#{id}

select PM.*, PT.ACTIVECLASS_NAME activeclassName, PS.SUPPLIER_NAME supplierName, PCI.LCC_NAME exportLccName, PC.LCC_NAME lccName, MS.STOCK_NAME stockName, MSS.STOCK_NAME exportStockName from PIP_MMS_DEVEXPORT_MASTER PM LEFT JOIN PIP_MMS_ACTIVE_TYPE PT ON PM.ACTIVECLASS_CODE=PT.ACTIVECLASS_CODE and PM.PROJECT_ID=PT.PROJECT_ID LEFT JOIN PIP_MMS_SUPPLIER PS ON PM.SUPPLIER_CODE=PS.SUPPLIER_CODE LEFT JOIN PIP_MMS_STOREHOUSE MS ON PM.STOCK_CODE=MS.STOCK_CODE and PM.PROJECT_ID=MS.PROJECT_ID LEFT JOIN PIP_MMS_STOREHOUSE MSS ON PM.EXPORT_STOCK_CODE=MSS.STOCK_CODE AND PM.PROJECT_ID=MSS.PROJECT_ID LEFT JOIN PIP_COMM_LCC PC ON PM.LCC_CODE=PC.LCC_CODE AND PM.PROJECT_ID=PC.PROJECT_ID LEFT JOIN PIP_COMM_LCC PCI ON PM.EXPORT_LCC_CODE=PCI.LCC_CODE AND PM.PROJECT_ID=PCI.PROJECT_ID where 1=1 and PM.IS_REMOVED=0
and PM.EXORDER_NO = #{exorderNo,jdbcType=VARCHAR}
and PM.PROJECT_ID = #{projectId,jdbcType=VARCHAR}
and MS.PROJECT_ID= #{projectId,jdbcType=VARCHAR}

select PD.*, PMI.MATERL_NAME materlName, PM.STOCK_CODE stockCode from PIP_MMS_DEVEXPORT_DETAIL PD left join PIP_MMS_DEVEXPORT_MASTER PM on PD.EXORDER_NO=PM.EXORDER_NO and PD.PROJECT_ID=PM.PROJECT_ID left join PIP_MMS_MATERL_INFO PMI on PD.MATERLINFO_CODE=PMI.MATERLINFO_CODE and PD.PROJECT_ID=PMI.PROJECT_ID where 1=1
and PD.PROJECT_ID = #{projectId,jdbcType=VARCHAR}
and PD.EXORDER_NO = #{exorderNo,jdbcType=VARCHAR}
and PD.ID = #{id,jdbcType=DECIMAL}

select
  DISTINCT PM.STOCK_CODE stockCode
from PIP_MMS_DEVEXPORT_DETAIL PD
left join PIP_MMS_DEVEXPORT_MASTER PM
on PD.EXORDER_NO=PM.EXORDER_NO and PD.PROJECT_ID=PM.PROJECT_ID
where 1=1

      and PD.PROJECT_ID = #{projectId,jdbcType=VARCHAR}


      and PD.EXORDER_NO = #{exorderNo,jdbcType=VARCHAR}


  	and PD.ID = #{id,jdbcType=DECIMAL}

  	  select MD.*,MI.MATERL_NAME materlName,MS.SUPPLIER_NAME supplierName,
  	    DM.export_date,exportLcc.lcc_code as export_lcc_code,exportLcc.lcc_name as export_lcc_name,DM.create_date as order_date
		from
		PIP_MMS_DEVEXPORT_DETAIL MD
		left join PIP_MMS_MATERL_INFO MI
		on MD.MATERLINFO_CODE=MI.MATERLINFO_CODE
		left join PIP_MMS_SUPPLIER MS
		ON MD.SUPPLIER_CODE=MS.SUPPLIER_CODE
		left join PIP_MMS_DEVEXPORT_MASTER DM on DM.exorder_no =MD.exorder_no and DM.project_id = MD.project_id
	  	left join pip_comm_lcc exportLcc on exportLcc.lcc_code = DM.export_lcc_code and DM.project_id = exportLcc.project_id
	  where 1=1

	  	and DM.LCC_CODE in (select lcc_code from pip_sys_user_data_limit  lt where lt.user_id = #{userId})



	  	and  MD.ARCHIVES_NO like '%'||#{archivesNo}||'%'


	  	and exportLcc.lcc_name like '%'||#{exportLccName}||'%'


	  	and exportLcc.lcc_code like '%'||#{exportLccCode}||'%'


	    and DM.export_date >= #{exportStartDate}


	    and DM.export_date <= #{exportEndDate}


	    and DM.create_date >= #{orderStartDate}


	    and DM.create_date <= #{orderEndDate}


       and MD.ID = = #{id,jdbcType=DECIMAL}


       and MD.PROJECT_ID = #{projectId,jdbcType=VARCHAR}


       and MD.EXORDER_NO = #{exorderNo,jdbcType=VARCHAR}


       and MD.MATERLINFO_CODE = #{materlinfoCode,jdbcType=VARCHAR}


		order by MD.${sidx} ${sord}

  	select count(*)
	 from PIP_MMS_DEVEXPORT_DETAIL PD
	 where PD.EXORDER_NO=#{exorderNo}

  	select PM.STOCK_CODE from PIP_MMS_DEVIMPORT_MASTER PM
		left join PIP_MMS_DEVIMPORT_DETAIL PD
		ON PM.PROJECT_ID=PD.PROJECT_ID AND PM.IMORDER_NO=PD.IMORDER_NO
	where 1=1
	and PM.PROJECT_ID=#{projectId}
	and PM.IMORDER_NO=#{imorderNo}
	and PD.ID=#{id}

select PS.*, PM.MATERL_NAME materlName, PM.HELP_CODE helpCode, PH.STOCK_NAME stockName, PL.LCC_NAME lccName, PMS.SUPPLIER_NAME supplierName from PIP_MMS_DEVSTORE PS left join PIP_MMS_MATERL_INFO PM on PS.MATERLINFO_CODE = PM.MATERLINFO_CODE and PS.PROJECT_ID = PM.PROJECT_ID left join PIP_MMS_STOREHOUSE PH on PS.STOCK_CODE = PH.Stock_Code and PH.PROJECT_ID = PS.PROJECT_ID left join PIP_COMM_LCC PL on PS.LCC_CODE = PL.Lcc_Code and PS.PROJECT_ID = PL.PROJECT_ID left join PIP_MMS_SUPPLIER PMS on PM.SUPPLIER_CODE=PMS.SUPPLIER_CODE where 1 = 1 and PS.LCC_CODE in (select lcc_code from pip_sys_user_data_limit lt where lt.user_id = #{userId}) and PM.PROJECT_ID=#{projectId} and PM.LCC_CODE=#{lccCode}
and ps.STORE_AMOUNT =1
and PH.STOCK_NAME like '%${stockName}%'
and PS.ARCHIVES_NO like '%${archivesNo}%'
and PM.MATERL_NAME like '%${materlName}%'
order by PS.ARCHIVES_NO desc, PS.${sidx} ${sord}

select PS.*, PM.MATERL_NAME materlName, PM.HELP_CODE helpCode, PH.STOCK_NAME stockName, PL.LCC_NAME lccName, PMS.SUPPLIER_NAME supplierName from PIP_MMS_DEVSTORE PS left join PIP_MMS_MATERL_INFO PM on PS.MATERLINFO_CODE = PM.MATERLINFO_CODE and PS.PROJECT_ID = PM.PROJECT_ID left join PIP_MMS_STOREHOUSE PH on PS.STOCK_CODE = PH.Stock_Code and PH.PROJECT_ID = PS.PROJECT_ID left join PIP_COMM_LCC PL on PS.LCC_CODE = PL.Lcc_Code and PS.PROJECT_ID = PL.PROJECT_ID left join PIP_MMS_SUPPLIER PMS on PM.SUPPLIER_CODE=PMS.SUPPLIER_CODE where 1 = 1 and PM.PROJECT_ID=#{projectId} and PM.LCC_CODE=#{lccCode}
and PH.STOCK_NAME like '%${stockName}%'
and PM.MATERL_NAME like '%${materlName}%'
order by PS.${sidx} ${sord}
