mapper.select:
    select 

    from PIP_MMS_STOREHOUSE
    where 1=1

    and LCC_CODE = #{lccCode,jdbcType=VARCHAR}


    and PROJECT_ID = #{projectId,jdbcType=VARCHAR}


    and STOCK_CODE = #{stockCode,jdbcType=VARCHAR}


    and STOCK_NAME like '%${stockName}%'


	order by ${sidx} ${sord}

select 

from PIP_MMS_STOREHOUSE
where 1=1

and LCC_CODE = #{lccCode,jdbcType=VARCHAR}


and PROJECT_ID = #{projectId,jdbcType=VARCHAR}


and STOCK_CODE = #{stockCode,jdbcType=VARCHAR}

mapper.delete:
delete from PIP_MMS_STOREHOUSE
where 1=1

  and PROJECT_ID = #{projectId,jdbcType=VARCHAR}
  and STOCK_CODE = #{stockCode,jdbcType=VARCHAR}

mapper.select:
  	select max(p.stock_code) from pip_mms_storehouse p
	where p.project_id=#{projectId,jdbcType=VARCHAR}
	and p.lcc_code= #{lccCode,jdbcType=VARCHAR}
